mapper.select:
select *
	from member 
	where mId=#{mId} and pw=#{pw}

select count(*)
	from member
	where mId=#{mId}

select count(*)
	from member
	where email=#{email}

select count(*)
	from member
	where email=#{email}
	and mId != #{mId}

mapper.update:
update MEMBER
    set pw=#{pw}, mName=#{mName}, phone=#{phone}, address=#{address}, email=#{email}
    where mid=#{mId}

update point
    set currentPoint=#{point}, updateDate=now()
    where mid=#{mId}

update member
    set mLevel=-1
    where mId=#{mId}

mapper.select:
select count(*)
	from member
	where mId=#{mId}
	and pw=#{pw}

select count(*)
	from member
	where email=#{email}

mapper.update:
update member 
	set mName=#{mName}, email=#{email}, phone=#{phone}, address=#{address}
	where mId=#{mId}

update member
	set pw=#{pw}
	where mId=#{mId}

update member
	set mLevel=-1
	where mId=#{mId}
